import React from 'react';
import Chart from '../Chart/Chart';

function ExpenseChart(props)	{

	const dataPointValues = props.dataPoints.map(dataPoint => dataPoint.value);
	const heighestValue = Math.max(dataPoint);

	const dataPoints =[
		{label: 'jan', value: 0},
		{label: 'feb', value: 0},
		{label: 'mar', value: 0},
		{label: 'apr', value: 0},
		{label: 'may', value: 0},
		{label: 'jun', value: 0},
		{label: 'jul', value: 0},
		{label: 'aug', value: 0},
		{label: 'sep', value: 0},
		{label: 'oct', value: 0},
		{label: 'nov', value: 0},
		{label: 'dec', value: 0}
	];

	for (const expense of props.expenses) {
		const expenseMonth = expense.date.getMonth();
		dataPoints[expenseMonth] += expense.Price;
	}

	return (
		<Chart dataPoints={dataPoints}/>	
	)
}

export default ExpenseChart;
