import React, {useState}from 'react';
import Card from '../UI/Card';
import classes from './ListAccounts.module.css';
import AccountStatement from './AccountStatement';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
} from "react-router-dom";

const DummyAccounts = [
	{id: "1", name:"Salman"},
	{id: "2", name:"Ali"},
	{id: "3", name:"Nobahar"}
];



function ListAccounts(props)	{
	const[hide,setHide] = useState(false);

	const updateNavHandler = (val) => {
		setHide(val);
	}

	return (
		<Card>
			{!hide && <button onClick={props.logout}>Logout</button>}
			{hide && <button >Go to all accounts</button>}
			<Router>
				{ !hide && 
					DummyAccounts.map(account => (
						<div key={account.id} className={classes.account} >

							<NavLink to={"/AccountStatement/" + account.name}>{account.name}</NavLink>

						</div>
					))
				}
				<Routes>
							<Route exact path="/AccountStatement/:accountId" element={<AccountStatement updateNav = {updateNavHandler} />}  />
				</Routes>
			</Router>
		</Card>
	)
}

export default ListAccounts;
