import React, { useRef, useEffect, useReducer} from 'react';
import classes from './LoginForm.module.css';
import Input from '../UI/Input';

const authReducer = (state, action) => {

	if (action.type = 'LOGIN') {
		return { email: action.email, password: action.password }		
	} else {
		return { email: '', password: '' }
	}

}

function LoginForm (props)	{

	const InitialUser = {email: '', password: ''};

	const emailRef = useRef();
	const passwordRef = useRef();

	const[authState, dispatch] =  useReducer(authReducer, InitialUser);

	const submitHandler = (e) => {
		e.preventDefault();
		// console.log(emailRef.current.value)
		dispatch({type:"LOGIN", email: emailRef.current.value, password: passwordRef.current.value })
	}

	useEffect (() => {
		emailRef.current.focus();
	}, []);

	return (
		<form className={classes.form} onSubmit={submitHandler}>
			<Input label="Email" ref={emailRef} input={{
				type: "email"
			}}/>
			<Input label="Password" ref={passwordRef} input={{type: "password"}}/>
			<button type="submit">Login</button>
			{authState.email}
		</form>
	)
}

export default LoginForm;
