import React,{ useEffect} from 'react';
import classes from './NavBar.module.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  useLocation
} from "react-router-dom";
let currLocation = '';
function NavBar(props)	{
	let location = useLocation();
	useEffect(()=>{
		currLocation = (location.pathname);
		console.log(currLocation)
	},[location.pathname])
	return (
		<>
		{console.log(currLocation.includes('AccountStatement'))}
	{ (currLocation === 'AccountStatement') && 	 
		<div key={props.account.id} className={classes.account} >
			<NavLink key={props.account.id} to={props.link}>{props.account.name}</NavLink>
		</div>
	}
	</>
	)
}

export default NavBar;
